export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {

  // const router = useRouter();
  const [isHome, setIsHome] = useState(false);
  useEffect(() => {
    if (typeof window !== 'undefined' && window.location.pathname === "/") {
      setIsHome(true);
    }
  }, []);

  const ScrollDownRef = useRef<HTMLDivElement>(null);

  const scrollToItem = (index: number) => {
    const container = ScrollDownRef.current;
    if (container) {
      const item = container.children[index] as HTMLElement;
      console.log(item.offsetTop);
      window.scrollTo({
        top: item.offsetTop,
        behavior: 'smooth',

      });

    }
  };

  const RenderChildrenOrPage = () => {
    if (isHome) {
      return <HomePage ScrollDownRef={ScrollDownRef}   />;
    } else {
      return children;
    }
  }



  
  return (
    <html lang="en">
      <Head>
        <meta charSet="utf-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <title>My Portfolio</title>
        <meta name="description" content={"Generated by Patarapong Charoenpol"} />
        <link rel="shortcut icon" href="/icons/favicon.ico" sizes="14" />
      </Head>
      <body className={inter.className}>
        <SpeedInsights />
        <div className=" relative ">
          <div className=" sticky top-0 z-[999] w-full">
            <Header scrollToItem= {scrollToItem}/>
          </div>
          {RenderChildrenOrPage()}
        </div>

      </body>
    </html>
  );
}
